export const IncreaseBasketProductCount = (basketIndex) => {
    return {
        type: "INCREASEBASKETPRODUCTCOUNT",
        payload: {
            basketIndex: basketIndex,
        }
    }
}

export const addProductIntoBasket = (product) => {
    return {
        type: "ADDPRODUCTINTOBASKET",
        payload: {
            product: product,
        }
    }
}
