let initializeState = {
    basket_items : [],
    basket_items_id : [],
};

export default (state = initializeState, action) => {
    if (action.type === "INCREASEBASKETPRODUCTCOUNT") {
        let basketIndex = action.payload.basketIndex;
        let newState    = {...state};
        newState.basket_items[basketIndex].count += 1;
        return newState;
    }
    return state;
}
